        "style": {
          "foreground": "#494d64"
        }
      },
      {
        "type": "time",
        "style": {
          "foreground": "#8aadf4"
        },
        "options": {
          "format": "HH:mm:ss",
          "use24Hour": true
        }
      },
      {
        "type": "separator",
        "content": " ",
        "style": {
          "foreground": "#494d64"
        }
      },
